package generalTest;

import javax.ws.rs.client.ClientBuilder;
import javax.ws.rs.client.Entity;
import javax.ws.rs.core.Form;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.MultivaluedHashMap;
import javax.ws.rs.core.MultivaluedMap;
import javax.ws.rs.core.Response;

import AssistPackage.Picture;

public class MultiThereadCallOM {

    public static void main(String[] args) {
        String targetRoute = "http://localhost:8080";
        String path = "/s2s/v1/v1/pictures";
        MultivaluedMap<String, Object> header = new MultivaluedHashMap<String, Object>();
        header.add("hybris_tenant", "vicky");
        Picture picture = new Picture();
        picture.setName("testPicture");
        String pic = "";
        picture.setPicContent(pic.getBytes());

        Form form = new Form();
        form.param("name", "testPicture");
        form.param("picContent", pic);

        Response response = ClientBuilder.newBuilder().build()
        .target(targetRoute).path(path).request()
        .headers(header)
        .post(Entity.entity(form,MediaType.APPLICATION_FORM_URLENCODED));

        System.out.println(response);

    }

}
